import React from 'react';
import { connect } from 'react-redux';
import { deleteFriend } from '../actions/DeleteFriendAction';
import { sortFriends } from '../actions/SortFriendsAction';
import Friend from './Friend';
import SortButton from './SortButton';

const FriendsGrid = ({
  friendList,
  deleteFriend,
  sortFriends,
}) => {
  console.log(friendList);
  return (<ul>
    {friendList.map(el => (
      <Friend
        id={el._id}
        key={el._id}
        name={el.firstName}
        lastName={el.lastName}
        age={el.age}
        deleteFriend={deleteFriend}
      />
    ))}
    <SortButton className="sort" sortFriends={sortFriends} />
  </ul>)
}

const mapStateToProps = ({ friendList }) => ({
  friendList,
});

const mapDispatchToProps = (dispatch) => ({
  deleteFriend: e => {
    e.persist();
    e.preventDefault();
    dispatch(deleteFriend(e.target.id));
  },
  sortFriends: e => {
    e.preventDefault();
    dispatch(sortFriends());
  },
});

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(FriendsGrid);
