import React from 'react';
import { Modal } from 'react-bootstrap';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import axios from 'axios';
import {
  updateFirstName,
  updateLastName,
  updateAge,
} from '../actions/UpdateFriendAction';
import { submitUpdate } from '../actions/SubmitUpdateAction';
import ModalFirstname from './ModalFirstname';
import ModalLastname from './ModalLastname';
import ModalAge from './ModalAge';
import ModalUpdateButton from './ModalUpdateButton';
import ModalHeader from './ModalHeader';
import ModalFooter from './ModalFooter';

class MyVerticallyCenteredModal extends React.Component {
  // constructor(props) {
  //   super(props);
  //   this.friendList = props.friendList;
  //   this.user = this.friendList.filter(usr => usr.id === this.id);
  // }

  state = {
    friendList: [],
  }

  componentDidMount() {
    axios.get('http://localhost:5000/friends/').then(res => {
      if (res.data.length > 0) {
        this.setState({
          friendList: res.data
        })
      }
    })
  }

  render() {
    const { id, submitUpdate, updateAge, updateFirstName, updateLastName } = this.props
    console.log(this.state.friendList);
    const friend = this.state.friendList.filter(friend => friend._id === id)
    return (
      <Modal
        {...this.props}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <ModalHeader />

        <Modal.Body>
          <form action="" onSubmit={submitUpdate} id={id}>
            <ModalFirstname
              firstName={friend.firstName}
              updateFirstName={updateFirstName}
            />
            <ModalLastname
              lastName={friend.lastName}
              updateLastName={updateLastName}
            />
            <ModalAge age={friend.age} updateAge={updateAge} />
            <ModalUpdateButton id={id} />
          </form>
        </Modal.Body>

        <ModalFooter hide={this.props.onHide} />
      </Modal>
    );
  }
}

const mapStateToProps = ({ friendList }) => ({
  friendList,
});

const mapDispatchToProps = dispatch => ({
  updateFirstName: e => {
    dispatch(updateFirstName(e.target.value));
  },
  updateLastName: e => {
    dispatch(updateLastName(e.target.value));
  },
  updateAge: e => {
    dispatch(updateAge(parseInt(e.target.value, 10)));
  },
  submitUpdate: e => {
    e.preventDefault();
    e.persist();
    dispatch(submitUpdate(e.target.id));
  },
});

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(MyVerticallyCenteredModal);
