import React from 'react';
import { connect } from 'react-redux';
import uuidv1 from 'uuid/v1';
import axios from 'axios';
import {
  addFirstName,
  addLastName,
  addAge,
} from '../actions/AddFriendAction';
import { submitFriend } from '../actions/submitFriendAction';
import AddButton from './AddButton';
import Firstname from './Firstname';
import Lastname from './Lastname';
import Age from './Age';

class FormField extends React.Component {

  sendDataToDB = (e) => {
    e.preventDefault();
    axios.post('http://localhost:5000/friends/add', this.props.friend)
      .then(res => console.log(res.data));

    window.location.reload();

  }

  render() {

    const { addFirstName,
      addLastName,
      addAge,
      submitFriend,
      friend } = this.props

    return (
      <div>
        <fieldset>
          <legend>Add a friend</legend>
          <form action="" onSubmit={submitFriend, this.sendDataToDB}>
            <Firstname addFirstName={addFirstName} />
            <Lastname addLastName={addLastName} />
            <Age addAge={addAge} />
            <AddButton />
          </form>
        </fieldset>
      </div>
    )
  }
}


const mapStateToProps = ({ friend }) => ({
  friend: friend
})

const mapDispatchToProps = dispatch => ({
  addFirstName: (e: *) => {
    dispatch(addFirstName(e.target.value));
  },
  addLastName: (e: *) => {
    dispatch(addLastName(e.target.value));
  },
  addAge: (e: *) => {
    dispatch(addAge(parseInt(e.target.value, 10)));
  },
  submitFriend: (e) => {
    e.preventDefault();
    const id = uuidv1();
    dispatch(submitFriend(id));
  },
});

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(FormField);
